sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {		
    },
	isDatePast:function(strDate)
	{
		var rightNow= new GlideDateTime();
		var testDate= new GlideDateTime(strDate);
		if(testDate.before(rightNow))
			{
				return true;
			}
		else
			{
				return false;
			}
	},
	isDateToday:function(strDate1)
	{
		var rightNow=new GlideDateTime();
		var testDate=new GlideDateTime(strDate1);
		if(rightNow.getLocalDate().compareTo(testDate.getLocalDate())==0)
			{
				return true;
			}
		else
			{
				return false;
			}
	},
    type: 'NeedItUtils'
};
